from . import BaseResource
from mds.core.models import Observation, Concept

import logging
logger = logging.getLogger('mds.sewi')

class ChartResource(BaseResource):
    """A representation of a chart Resource.
    """

    __ERROR_RESOURCE_NOT_CHART = 'Resource is not a chart.'
    __CHART_TYPE_STRING = 'chart'
    __CHART_ICON_BASE64 = 'data:image/jpeg;base64,'

    def __init__(self, resource_id):
        self.__observation = Observation.objects.get(uuid=resource_id)

        if not self.is_valid_observation(self.__observation):
            raise ValueError(self.__ERROR_RESOURCE_NOT_CHART)
        
        self.__concept = self.__observation.concept
        logger.debug('Chart observation successfully retrieved: %s' % resource_id)

        self.__url = self.__observation.value_complex.url
        self.__mimetype = self.__concept.mimetype

    @classmethod
    def is_valid_observation(cls, observation):
        return cls.__CHART_TYPE_STRING in observation.concept.mimetype

    def get_info(self):
        return {
            'url': self.get_content(),
            'type': self.get_type()
        }

    def get_content(self):
        return self.__url;

    def get_type(self):
        return self.__mimetype

    def generate_thumbnail(self):
        return self.__CHART_ICON_BASE64
